CREATE TABLE `MyDb`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `lname` VARCHAR(100) NOT NULL , `fname` VARCHAR(100) NOT NULL , `email` VARCHAR(100) NOT NULL , `password` VARCHAR(100) NOT NULL , `profile` VARCHAR(100) NOT NULL , `unique_id` VARCHAR(100) NOT NULL , `date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = MyISAM;
